-- dump_columns.sql
-- psql $SUPABASE_DB_URL -f scripts/dump_columns.sql
\copy (SELECT table_name, column_name, data_type, is_nullable, column_default FROM information_schema.columns WHERE table_schema = 'public' AND table_name IN ('business_assets', 'completed_posts', 'content_creation_tasks', 'ingested_event_sources', 'ingested_events', 'insight_reports', 'media', 'news_event_seed_sources', 'news_event_seeds', 'platform_comments', 'sources', 'trend_seeds', 'ungrounded_seeds') ORDER BY table_name, ordinal_position) TO './table_columns.csv' WITH CSV HEADER;